$(document).ready(function(){let dropzone=$("#dropzone"),input=dropzone.find('input');$('#remove').addClass('d-none');dropzone.on({dragenter:dragin,dragleave:dragout});input.on('change',drop);function dragin(e){$(dropzone).addClass('hover');}
    function dragout(e){$(dropzone).removeClass('hover');}
    function drop(e){let file=this.files[0];$('#dropzone').removeClass('hover').addClass('dropped').find('img').remove();$('#remove').removeClass('d-none')
        viewImage(file);}
    function viewImage(file){let reader=new FileReader(file);reader.readAsDataURL(file);reader.onload=function(e){$('#dropzone div').html($('<img  alt="attachment"/>').attr('src',e.target.result).fadeIn());}}
    $('#remove').on('click',function(){$('#dropzone').find('img').remove();$('#remove').addClass('d-none')})});